INSERT INTO `cmd_tokusho` (`id`, `shop_id`, `delivery`, `delivery_time`, `receipt`, `rcx_overview`, `cancel`, `refund`, `exchange`, `payment_method`) VALUES
(1,	22,	'【業者】 ヤマト運輸\r\n【配送サービス名】宅急便\r\n\r\n海外配送について\r\n　当店は海外へ発送を行っていません',	NULL,	NULL,	NULL,	NULL,	NULL,	'不良品による返金\r\n全額ご返金いたします\r\n商品到着より8日以内に当店までご連絡下さい。\r\nクレジットカードは請求を取り消します。\r\n代引き、前払いは現金書留か郵便小為替、お手持ちの銀行および郵便口座への振込みのいずれかをご指定いただき返金いたします。',	NULL),
(2,	24,	'クロネコヤマト便にて発送いたします。\r\n配送料\r\n\r\n1.通常配送\r\n\r\n北海道 1260円\r\n\r\n北東北（青森・秋田・岩手）940円\r\n\r\n南東北（宮城県・山形県・福島県）840円\r\n\r\n関東 / 甲信越 840円\r\n中部 840円\r\n北陸 840円\r\n関西 940円\r\n中国 1050円\r\n四国 1150円\r\n九州 1260円\r\n沖縄 1360円',	'入金確認後３日以内に発送します。\r\n但し、在庫不足品に関しては別途メールにてお知らせいたします。',	'領収書の有無は注文時のメモ欄に記載いただくか、発送前までにメールにてお知らせください。',	'商品配送後のお客様都合による返品、交換は受付けておりません。\r\n下記の場合はこの限りではございませんのでヤマダモール joys運営管理部へメールにてお問い合わせ下さい。\r\n- 梱包箱や商品の破損があり輸送事故が疑われる場合\r\n- 商品の初期不良等の不具合\r\nお客様都合での送付後の住所変更の場合に発生する送料は お客様のご負担となります。',	'発送前にご連絡を頂ければキャンセルいたします。\r\n但し、キャンセル料が発生します。',	'返品は基本的にできません。\r\n但し、初期不良品と弊社が判断でしたものに関しては返品させて頂きます。',	'基本的に取り扱っていません。\r\n但し、弊社が認める場合は送料をお客様で負担いただき対応いたします。',	'tatata'),
(3,	19,	NULL,	NULL,	NULL,	NULL,	NULL,	NULL,	NULL,	'支払い方法を入力'),
(4,	20,	NULL,	NULL,	NULL,	NULL,	NULL,	NULL,	NULL,	'お支払い方法の追加のテスト'),
(5,	42,	'■宅配便\r\n【運送会社】\r\n［ヤマト運輸］でのお届けになります。（「日本郵便」でのお届けの場合もございます。）\r\n※ご指定いただくことはできません。\r\n\r\n【配送希望時間帯をご指定出来ます】\r\n午前中・14時～16時・16時～18時・18時～20時・19時～21時\r\nただし時間を指定された場合でも、事情により指定時間内に配達ができない事もございます。\r\n\r\n【送料料金表】\r\n\r\n商品価格：\r\n～3,979円（税込）　	商品価格：\r\n3,980 ～ 9,799円（税込）　	商品価格：\r\n9,800円（税込）～　\r\n北海道	2,090円（税込）	0円（税込）	0円（税込）\r\n沖縄	3,850円（税込）	3,850円（税込）	0円（税込）\r\n本州・四国・九州	880円（税込）	0円	0円\r\n※送料別の商品：商品価格に送料が含まれていないもので、かつ、商品ページで個別に送料が設定されていない商品。\r\n※送料込の商品：商品価格に送料が含まれているもの。商品ページで個別に送料が設定されている商品を含みます。\r\n\r\n【送料無料商品の扱い】\r\n１配送先につき、送料無料の商品と送料別の商品を同時にご注文の場合、送料は上記料金表に関係なく送料無料になります。ただし、商品ページで個別に送料が設定されている商品がある場合は、その送料×個数分は必要になります。\r\n\r\n【送料分消費税】\r\nこの料金には消費税が含まれています。\r\n\r\n【離島他の扱い】\r\n離島・一部地域でも追加送料がかかることはありません。',	NULL,	NULL,	NULL,	'ご注文確定後のキャンセル・注文内容の変更、商品の交換はできません。\r\n\r\n・弊社が定めるご発注の一定量を超えるご注文は、ご注文をお断りさせていただく場合がございますので、予めご了承くださいませ。\r\n\r\n・ご注文頂いた商品の在庫数が少量及び注文が殺到した場合、システム処理にタイムラグが生じ、欠品が発生する可能性がございます。\r\n欠品した商品を除いたご注文内容で配送させて頂く場合や、ご注文をキャンセルさせていただく場合がございます。予めご了承ください。',	'・お客様都合による返品\r\nお客様都合による返品の場合は「14日以内」にお電話、またはメールにて必ずご連絡をお願いします。\r\nご連絡を頂いてから商品の返品、返金方法をご説明いたします。送料はお客様負担とさせていただきます。\r\n\r\n・不良品などによる返品\r\n商品到着後14日以内にテーラーメイド ゴルフ カスタマーサービスコールまでご連絡の上、当該商品を指定場所へ返送して下さい。この場合の返品送料は当社が負担いたします。\r\n在庫状況により、他の商品への変更をお願いする場合がありますのでご了承下さい。\r\n交換品は返品商品を受領した後に発送いたします。\r\n交換品がご購入商品と異なり差額が生じる場合は、ご購入金額を返金させて頂きます。あらためて交換品をご注文下さい。',	'・お客様都合による返品\r\nお客様都合による返品の場合は「14日以内」にお電話、またはメールにて必ずご連絡をお願いします。\r\nご連絡を頂いてから商品の返品、返金方法をご説明いたします。送料はお客様負担とさせていただきます。\r\n\r\n・不良品などによる返品\r\n商品到着後14日以内にテーラーメイド ゴルフ カスタマーサービスコールまでご連絡の上、当該商品を指定場所へ返送して下さい。この場合の返品送料は当社が負担いたします。\r\n在庫状況により、他の商品への変更をお願いする場合がありますのでご了承下さい。\r\n交換品は返品商品を受領した後に発送いたします。\r\n交換品がご購入商品と異なり差額が生じる場合は、ご購入金額を返金させて頂きます。あらためて交換品をご注文下さい。',	'以下のお支払い方法をご用意しております。\r\n\r\n・クレジットカード\r\n・代金引換（現金のみ）\r\n・銀行振込\r\n\r\n※クレジットカードのセキュリティはSSLというシステムを利用しております。 カード番号は暗号化されて安全に送信されますので、どうぞご安心ください。');
